import { Animated, ColorValue, StyleSheet, TextInput } from 'react-native';
import Svg, { Color, Path } from 'react-native-svg';
import { osRtl } from './utils';

interface KnobProps {
  translateX: Animated.Value;
  scale: Animated.Value;
  knobSize: number;
  textInputRef: React.RefObject<TextInput>;
  valueLabelsBackgroundColor: Color | undefined;
  valueLabelsTextColor: ColorValue | undefined;
}

const KnobBubble = ({ translateX, scale, knobSize, valueLabelsBackgroundColor: fill, valueLabelsTextColor: color, textInputRef }: KnobProps) => {
