      <TextInput editable={false} style={[styles.textInput, svgOffset, { color }]} ref={textInputRef} />
    </Animated.View>
  )
}

const styles = StyleSheet.create({
  container: {
    position: 'absolute',
    bottom: 0,
    left: 0
  },
  textInput: {
    position: 'absolute',
    width: 40,
    textAlign: 'center',
    bottom: 25,
    fontWeight: 'bold'
  }
})

export default KnobBubble;
